INSERT INTO `mdl_facetoface_notification_tpl` (`id`, `title`, `body`, `managerprefix`, `status`, `reference`) VALUES
(1, 'Face-to-face booking confirmation: [facetofacename], [starttime]-[finishtime], [sessiondate]', '<div class="text_to_html">This is to confirm that you are now booked on the following course:<br />\r\n<br />\r\nParticipant:   [firstname] [lastname]<br />\r\nCourse:   [coursename]<br />\r\nFace-to-face:   [facetofacename]<br />\r\nCost:   [cost]<br />\r\n<br />\r\nDuration:    [duration]<br />\r\nDate(s):<br />\r\n[alldates]<br />\r\n<br />\r\nLocation:   [session:location]<br />\r\nVenue:   [session:venue]<br />\r\nRoom:   [session:room]<br />\r\n<br />\r\n***Please arrive ten minutes before the course starts***<br />\r\n<br />\r\nTo re-schedule or cancel your booking<br />\r\nTo re-schedule your booking you need to cancel this booking and then re-book a new session.  To cancel your booking, return to the site, then to the page for this course, and then select ''cancel'' from the booking information screen.<br />\r\n<br />\r\n[details]<br />\r\n</div>', '<div class="text_to_html">*** Advice only ****<br />\r\n<br />\r\nThis is to advise that [firstname] [lastname] has been booked for the following course and listed you as their Team Leader / Manager.<br />\r\n<br />\r\nIf you are not their Team Leader / Manager and believe you have received this email by mistake please reply to this email.  If you have concerns about your staff member taking this course please discuss this with them directly.<br />\r\n<br />\r\n</div>', 1, 'confirmation'),
(2, 'Face-to-face booking cancellation', '<div class="text_to_html">This is to advise that your booking on the following course has been cancelled:<br />\r\n<br />\r\n***BOOKING CANCELLED***<br />\r\n<br />\r\nParticipant:   [firstname] [lastname]<br />\r\nCourse:   [coursename]<br />\r\nFace-to-face:   [facetofacename]<br />\r\n<br />\r\nDuration:   [duration]<br />\r\nDate(s):<br />\r\n[alldates]<br />\r\n<br />\r\nLocation:   [session:location]<br />\r\nVenue:   [session:venue]<br />\r\nRoom:   [session:room]<br />\r\n</div>', '<div class="text_to_html">*** Advice only ****<br />\r\n<br />\r\nThis is to advise that [firstname] [lastname] is no longer signed-up for the following course and listed you as their Team Leader / Manager.<br />\r\n<br />\r\n</div>', 1, 'cancellation'),
(3, 'Waitlisting advice for [facetofacename]', '<div class="text_to_html">This is to advise that you have been added to the waitlist for:<br />\r\n<br />\r\nCourse:   [coursename]<br />\r\nFace-to-face:   [facetofacename]<br />\r\nLocation:  [session:location]<br />\r\nParticipant:   [firstname] [lastname]<br />\r\n<br />\r\n***Please note this is not a course booking confirmation***<br />\r\n<br />\r\nBy waitlisting you have registered your interest in this course and will be contacted directly when sessions become available.<br />\r\n<br />\r\nTo remove yourself from this waitlist please return to this course and click Cancel waitlist. Please note there is no waitlist removal confirmation email.<br />\r\n</div>', NULL, 1, 'waitlist'),
(4, 'Face-to-face booking reminder: [facetofacename], [starttime]-[finishtime], [sessiondate]', '<div class="text_to_html">This is a reminder that you are booked on the following course:<br />\r\n<br />\r\nParticipant:   [firstname] [lastname]<br />\r\nCourse:   [coursename]<br />\r\nFace-to-face:   [facetofacename]<br />\r\nCost:   [cost]<br />\r\n<br />\r\nDuration:   [duration]<br />\r\nDate(s):<br />\r\n[alldates]<br />\r\n<br />\r\nLocation:   [session:location]<br />\r\nVenue:   [session:venue]<br />\r\nRoom:   [session:room]<br />\r\n<br />\r\n***Please arrive ten minutes before the course starts***<br />\r\n<br />\r\nTo re-schedule or cancel your booking<br />\r\nTo re-schedule your booking you need to cancel this booking and then re-book a new session.  To cancel your booking, return to the site, then to the page for this course, and then select ''cancel'' from the booking information screen.<br />\r\n<br />\r\n[details]<br />\r\n</div>', '<div class="text_to_html">*** Reminder only ****<br />\r\n<br />\r\nYour staff member [firstname] [lastname] is booked to attend and above course and has also received this reminder email.<br />\r\n<br />\r\nIf you are not their Team Leader / Manager and believe you have received this email by mistake please reply to this email.<br />\r\n<br />\r\n</div>', 1, 'reminder'),
(5, 'Face-to-face booking request: [facetofacename], [starttime]-[finishtime], [sessiondate]', '<div class="text_to_html">Your request to book into the following course has been sent to your manager:<br />\r\n<br />\r\nParticipant:   [firstname] [lastname]<br />\r\nCourse:   [coursename]<br />\r\nFace-to-face:   [facetofacename]<br />\r\nCost:   [cost]<br />\r\n<br />\r\nDuration:   [duration]<br />\r\nDate(s):<br />\r\n[alldates]<br />\r\n<br />\r\nLocation:   [session:location]<br />\r\nVenue:   [session:venue]<br />\r\nRoom:   [session:room]<br />\r\n</div>', '<div class="text_to_html">This is to advise that [firstname] [lastname] has requested to be booked into the following course, and you are listed as their Team Leader / Manager.<br />\r\n<br />\r\nPlease follow the link below to approve the request:<br />\r\n[attendeeslink]<br />\r\n<br />\r\n</div>', 1, 'request'),
(6, 'Face-to-face booking decline', '<div class="text_to_html">This is to advise that your booking on the following course has been declined:<br />\r\n<br />\r\n***BOOKING DECLINED***<br />\r\n<br />\r\nParticipant:   [firstname] [lastname]<br />\r\nCourse:   [coursename]<br />\r\nFace-to-face:   [facetofacename]<br />\r\n<br />\r\nDuration:   [duration]<br />\r\nDate(s):<br />\r\n[alldates]<br />\r\n<br />\r\nLocation:   [session:location]<br />\r\nVenue:   [session:venue]<br />\r\nRoom:   [session:room]<br />\r\n</div>', '<div class="text_to_html">*** Advice only ****<br />\r\n<br />\r\nThis is to advise that [firstname] [lastname] is no longer signed-up for the following course and listed you as their Team Leader / Manager.<br />\r\n<br />\r\n</div>', 1, 'decline'),
(7, 'Face-to-face booking date/time changed: [facetofacename], [starttime]-[finishtime], [sessiondate]', '<div class="text_to_html">Your session date/time has changed:<br />\r\n<br />\r\nParticipant:   [firstname] [lastname]<br />\r\nCourse:   [coursename]<br />\r\nFace-to-face:   [facetofacename]<br />\r\n<br />\r\nDuration:   [duration]<br />\r\nDate(s):<br />\r\n[alldates]<br />\r\n<br />\r\nLocation:   [session:location]<br />\r\nVenue:   [session:venue]<br />\r\nRoom:   [session:room]<br />\r\n</div>', NULL, 1, 'timechange'),
(8, 'Face-to-face session trainer cancellation', '<div class="text_to_html">This is to advise that your assigned training session the following course has been cancelled:<br />\r\n<br />\r\n***SESSION CANCELLED***<br />\r\n<br />\r\nParticipant:   [firstname] [lastname]<br />\r\nCourse:   [coursename]<br />\r\nFace-to-face:   [facetofacename]<br />\r\n<br />\r\nDuration:   [duration]<br />\r\nDate(s):<br />\r\n[alldates]<br />\r\n<br />\r\nLocation:   [session:location]<br />\r\nVenue:   [session:venue]<br />\r\nRoom:   [session:room]<br />\r\n</div>', NULL, 1, 'trainercancel'),
(9, 'Face-to-face session trainer unassigned', '<div class="text_to_html">This is to advise that you have been unassigned from training for following course:<br />\r\n<br />\r\n***SESSION UNASSIGNED***<br />\r\n<br />\r\nParticipant:   [firstname] [lastname]<br />\r\nCourse:   [coursename]<br />\r\nFace-to-face:   [facetofacename]<br />\r\n<br />\r\nDuration:   [duration]<br />\r\nDate(s):<br />\r\n[alldates]<br />\r\n<br />\r\nLocation:   [session:location]<br />\r\nVenue:   [session:venue]<br />\r\nRoom:   [session:room]<br />\r\n</div>', NULL, 1, 'trainerunassign'),
(10, 'Face-to-face trainer confirmation: [facetofacename], [starttime]-[finishtime], [sessiondate]', '<div class="text_to_html">This is to confirm that you are now assigned to deliver training on the following course:<br />\r\n<br />\r\nParticipant:   [firstname] [lastname]<br />\r\nCourse:   [coursename]<br />\r\nFace-to-face:   [facetofacename]<br />\r\n<br />\r\nDuration:    [duration]<br />\r\nDate(s):<br />\r\n[alldates]<br />\r\n<br />\r\nLocation:   [session:location]<br />\r\nVenue:   [session:venue]<br />\r\nRoom:   [session:room]<br />\r\n<br />\r\n***Please arrive ten minutes before the course starts***<br />\r\n<br />\r\n[details]<br />\r\n</div>', NULL, 1, 'trainerconfirm'),
(11, 'All reservations cancelled', '<div class="text_to_html">This is to advise you that all unallocated reservations for the following course have been automatically cancelled, as the course will be starting soon:<br />\r\n<br />\r\n***ALL RESERVATIONS CANCELLED***<br />\r\n<br />\r\nCourse:   [facetofacename]<br />\r\n<br />\r\nDuration:   [duration]<br />\r\nDate(s):<br />\r\n[alldates]<br />\r\n<br />\r\nLocation:   [session:location]<br />\r\nVenue:   [session:venue]<br />\r\nRoom:   [session:room]<br />\r\n</div>', NULL, 1, 'allreservationcancel'),
(12, 'Reservation cancellation', '<div class="text_to_html">This is to advise you that your reservation for the following course has been cancelled:<br />\r\n<br />\r\n***RESERVATION CANCELLED***<br />\r\n<br />\r\nCourse:   [facetofacename]<br />\r\n<br />\r\nDuration:   [duration]<br />\r\nDate(s):<br />\r\n[alldates]<br />\r\n<br />\r\nLocation:   [session:location]<br />\r\nVenue:   [session:venue]<br />\r\nRoom:   [session:room]</div>', NULL, 1, 'reservationcancel');

